"use strict";
var host = (document.domain.indexOf("biligame.com") > 0) ? "//line1-h5-pc-api.biligame.com" :
	"//le3-api.game.bilibili.com/pc";
var isFromNewPCBack = window.location.href.indexOf("version_flag_game") !== -1;
var isFromNewPC = window.location.href.indexOf("hreffrom") !== -1;
if (window.localStorage && isFromNewPCBack) {
	window.localStorage.setItem("clientVersion", "old")
}
if (window.localStorage && isFromNewPC) {
	window.localStorage.removeItem("clientVersion")
}
var needCheckGrey = !(localStorage.getItem("clientVersion") || false);
if (needCheckGrey) {
	$.ajax({
		url: host + "/gray/is_gray",
		type: "Get",
		crossDomain: true,
		xhrFields: {
			withCredentials: true
		},
		success: function success(res) {
			if (res.code === 0 && res.data) {
				if (!isFromNewPCBack && !isFromNewPC) {
					window.location.href = "//game.bilibili.com/platform"
				}
			}
		}
	})
} else {
	window.setButtonBack = true
};
